+        setError(true);
+        setErrorType('load');
+      }
+    };
+
+    fetchTodos();
+  }, []);
+
+  useEffect(() => {
+    if (focus) {
+      const inputField = document.querySelector(
+        '.todoapp__new-todo',
+      ) as HTMLElement;
+
+      if (inputField) {
+        inputField.focus();
+      }
+    }
+  }, [focus]);
+
+  if (!USER_ID) {
+    return <UserWarning />;
+  }
+
+  const handleKeyDown = async (
+    event: React.KeyboardEvent<HTMLInputElement>,
+  ) => {
+    const trimmedTodo = newTodoTitle.trim();
+
+    if (event.key === 'Enter') {
+      event.preventDefault();
+
+      if (trimmedTodo) {
+        setFocus(false);
+
+        try {
+          setAddNewTodo(true);
+
+          const newTodo: Todo = {
+            id: 0,
+            userId: USER_ID,
+            title: trimmedTodo,
+            completed: false,
+          };
+
+          setTempTodo(newTodo);
+
+          const response = await postTodo(newTodo);
+
+          setLoadingAddTodoId(response.id);
+
+          setTodos(prevTodos => [...prevTodos, response]);
+          setNewTodoTitle('');
+          setTempTodo(null);
+          setAddNewTodo(false);
+
+          setLoadingAddTodoId(null);
+          setFocus(true);
+        } catch (err) {
+          setError(true);
+          setErrorType('add');
+          setAddNewTodo(false);
+          setFocus(true);
+        }
+      } else {
+        setError(true);
+        setErrorType('empty');
+        setFocus(true);
